mapper.select:
select comment_no as commentNo, user_id as userId, comment_content as commentContent, reg_date as regDate
from comment
where comment_no = #{commentNo}

select comment_no as commentNo, user_id as userId, comment_content as commentContent, reg_date as regDate
from comment
where comment_no >= #{commentNo}

mapper.update:
update comment
set comment_content = #{commentContent}
where comment_no = #{commentNo}
